 */
class Category extends BaseCategory
{
  /**
   * Returns indented name.
   *
   * @return String - indented name
   */
  public function getIndentedName()
  {
    return ($this['parent_id'] ? '&nbsp;&nbsp;&nbsp;' : '').$this->getName();
  }

  /**
   * Returns total cash spent on a particular Category.
   *
   * @return Decimal
   */
  public function getAmountSum()
  {
    $sum = 0;
    foreach ($this->getOutcomes() as $outcome)
    {
      $sum += $outcome->getAmount();
    }
    return $sum;
  }
}
