<?php

/**
 * OutcomeTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class OutcomeTable extends DuckDoctrineTable
{
  /**
   * Returns an instance of this class.
   *
   * @return object OutcomeTable
   */
  public static function getInstance()
  {
    return Doctrine_Core::getTable('Outcome');
  }

  /**
   * Returns array of monthly sums of all outcomes for a given date range,
   * optionally filtered by user.
   *
   * @param String $date_from
   * @param String $date_to
   * @param Integer $created_by
   * @return Array
   */
  public static function getOutcomeMonthlySumByDateRange($date_from, $date_to, $created_by = null)
  {
    $query = Doctrine_Query::create()
      ->select('SUM(o.amount) AS sum, DATE_FORMAT( o.created_at, "%Y-%m" ) AS date')
      ->from('Outcome o')
      ->where("o.created_at BETWEEN '{$date_from}' AND '{$date_to}'")
      ->groupBy('YEAR(o.created_at)')
      ->addGroupBy('MONTH(o.created_at)')
      ->orderBy('o.created_at ASC');
    if ($created_by) $query->andWhere('o.created_by = ?', $created_by);
    return $query->fetchArray();
  }

  /**
   * Returns query retrieving Outcome objects with related data. Method used in
   * symfony/doctrine admin backend module list.
   *
   * @return object Doctrine_Query
   */
  public static function getOutcomesBackendListQuery()
  {
    return Doctrine_Query::create()
      ->from('Outcome o')
      ->leftJoin('o.Category ctg')
      ->leftJoin('o.Creator cr')
      ->orderBy('o.created_at DESC');
  }

  public function typecast(&$outcome) {
    $outcome['id'] = (int) $outcome['id'];
    $outcome['category_id'] = (int) $outcome['category_id'];
    $outcome['created_by'] = (int) $outcome['created_by'];
    $outcome['amount'] = (float) $outcome['amount'];
  }
}